--drop table COMPANY;
create table COMPANY (
	"ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
	"NAME" VARCHAR(20) NOT NULL,
 	"ADDRESS" VARCHAR(20)
);

create table ARTICLE_TYPE (
	"ARTICLE_TYPE_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
	"DESCRIPTION" VARCHAR(20) NOT NULL,
);

create table CATEGORY (
	"CATEGORY_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
	"NAME" VARCHAR(20) NOT NULL
);

create table ARTICLE (
	"ARTICLE_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
	"TITLE" VARCHAR(20) NOT NULL,
	"CONTENT" VARCHAR(20) NOT NULL,
	"ARTICLE_TYPE_ID" INTEGER NOT NULL
);

create table ARTICLE_CATEGORY_RELATION (
	"ARTICLE_ID" INTEGER NOT NULL,
	"CATEGORY_ID" INTEGER NOT NULL,
	PRIMARY KEY("ARTICLE_ID", "CATEGORY_ID")
);

create table COMMENT (
	"COMMENT_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
	"TEXT" VARCHAR(20) NOT NULL,
	"ARTICLE_ID" INTEGER NOT NULL
);

